import java.util.HashMap;
import java.util.Map;
import java.util.ArrayList;
import java.util.List;

class Solution{
    //This solution is incorrect for this problem
	public int numMatchingSubseq_incorrect(String S, String[] words){
        Map<Character,Integer> map = new HashMap<>();
        int count = 0;
        if(words == null || S == null || S.length() == 0 || words.length == 0)
            return count;
        for(char c : S.toCharArray()){
            int x = map.getOrDefault(c,0) + 1;
            map.put(c, x);
        }
        int[] arr = new int[26];
        int t = 0;
        boolean is_break = false;
        for(String s : words){
            for(char c : s.toCharArray()){
                if(!map.containsKey(c)){
                    is_break = true;
                    break;
                }
                else{
                    t = map.get(c);
                    if(t < 1){
                        is_break = true;
                        break;
                    }
                    else{
                        map.put(c, t-1);
                        arr[c-'a'] += 1;
                    }
                }
            }
            if(!is_break){
                count++;
            }else{
                is_break = false;
            }
            //now restore the hashmap
            for(int i=0; i<26; i++){
                if(arr[i] != 0){
                    t = map.get((char)(i+'a'));
                    map.put((char)(i+'a'), t+arr[i]);
                } 
            }
        }

        return count;
	}	
    //using index pairs (i,j) meaning word i is waiting for its letter j
	public int numMatchingSubseq(String S, String[] words){
        List<Integer[]>[] waiting = new List[128];
        for(int c=0; c<='z'; c++)
            waiting[c] = new ArrayList<>();
        for(int i=0; i<words.length; i++)
            waiting[words[i].charAt(0)].add(new Integer[]{i,1});
        for(char c : S.toCharArray()){
            List<Integer[]> advance = new ArrayList<>(waiting[c]);
            waiting[c].clear();
            for(Integer[] a : advance)
                waiting[a[1] < words[a[0]].length() ? words[a[0]].charAt(a[1]++) : 0].add(a);
        }

        return waiting[0].size();
    }
}

